import React, { Component } from 'react';
import SwapiService from '../../services/SwapiService';
import Loading from '../Loading';
import ErrorIndicator from '../Alerts/ErrorIndicator';
import './RandomPlanet.css';

export default class RandomPlanet extends Component {
    SwapiService = new SwapiService();
    state = {
        planet: {},
        loading: true,
        error: false
    }
    constructor() {
        super()
        this.updatePlanet();
    }
    onError = (err) => {
        this.setState({
            error: true
        })
    }
    onPlanetLoaded = (planet) => {
        this.setState({
            planet,
            loading: false
        })
    }
    updatePlanet() {
        const id = Math.floor(Math.random() * 25) + 2;
        this.SwapiService
            .getPlanet(id)
            .then(this.onPlanetLoaded)
            .catch(this.onError)
    }
    render() {
        const { planet, loading, error } = this.state;
        const ErrorIndicator = error ? <ErrorIndicator /> : null
        const spinner = loading && !error ? <Loading /> : null
        const content = !loading ? <PlanetView planet={planet} /> : null
        return (
            <div className="random-planet jumbotron rounded" >
                {spinner}
                {content}
            </div>

        );
    }
}
const PlanetView = ({ planet }) => {
    const { id, name, population, rotationPeriod, diameter } = planet;
    return (
        <React.Fragment>
            <img className="planet-image"
                src={`https://starwars-visualguide.com/assets/img/planets/${id}.jpg`} alt={`https://starwars-visualguide.com/assets/img/planets/${id}.jpg`} />
            <div>
                <h4>{name}</h4>
                <ul className="list-group list-group-flush">
                    <li className="list-group-item">
                        <span className="term">Population</span>
                        <span>{population}</span>
                    </li>
                    <li className="list-group-item">
                        <span className="term">Rotation Period</span>
                        <span>{rotationPeriod}</span>
                    </li>
                    <li className="list-group-item">
                        <span className="term">Diameter</span>
                        <span>{diameter}</span>
                    </li>
                </ul>
            </div>
        </React.Fragment>
    );
}
